import React, { useState} from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import {COVID19ModalHeaderText, COVID19ModalText} from '../constants';

const HomePage = ()=>{

    const [modal, setModal] = useState(false);
    
  const toggle = () =>{
    setModal(!modal);
   }

        return(
            <>
                <div className = "container">
                <Button color="danger" onClick={toggle}> View Modal </Button>
                <Modal isOpen = {modal} toggle = {toggle} >
                    <ModalHeader toggle = {toggle}> {COVID19ModalHeaderText} </ModalHeader>
                    <ModalBody>
                        {COVID19ModalText}
                    </ModalBody>
                    ModalFooter
                </Modal>
                </div>
            </>
        )
}

export default HomePage;
